mapper.select:
SELECT CASE c.CONSTANT_TYPE WHEN 'P_TYPE' THEN '专业' WHEN 'C_TYPE' THEN '子专业' WHEN 'REPAIR_LEVEL' THEN '检修级别' END as CONSTANT_TYPE, c.CONSTANT_TYPE as CONSTANT_TYPE, c.CONSTANT_NO as CONSTANT_NO, c.CONSTANT_NAME as CONSTANT_NAME, c1.CONSTANT_NAME as PARENT_NO, c.SORT as SORT, c.ATTRIBUTE_1 as ATTRIBUTE_1, c.ATTRIBUTE_2 as ATTRIBUTE_2, c.ATTRIBUTE_3 as ATTRIBUTE_3, c.DELETE_FLAG as DELETE_FLAG, c.CREATE_BY as CREATE_BY, c.CREATE_TIME as CREATE_TIME, c.LAST_MODIFY_BY as LAST_MODIFY_BY, c.LAST_MODIFY_TIME as LAST_MODIFY_TIME FROM constant c LEFT JOIN constant c1 ON c1.CONSTANT_NO = c.PARENT_NO AND c1.CONSTANT_TYPE = 'P_TYPE' AND c1.`DELETE_FLAG` = 0
c.`DELETE_FLAG` = 0
and c.`CONSTANT_TYPE` = #{constantType}
and c.`CONSTANT_NO` = #{constantNo}
and c.`CONSTANT_NAME` = #{constantName}
and c.`PARENT_NO` = #{parentNo}
and c.`SORT` = #{sort}
order by CONSTANT_TYPE,CONSTANT_NO,SORT LIMIT ${startNo}, ${pageSize}

	  
	 where delete_flag = '0'
	    
  	  and `CONSTANT_TYPE` = #{constantType}      
   
      
  	  and `PARENT_NO` = #{parentNo}      
   
	         
	 order by SORT

SELECT CONSTANT_NAME,CONSTANT_NO ,CONSTANT_TYPE FROM constant C
where
	C.CONSTANT_TYPE = #{constantType}

update.foreach:
  UPDATE constant
  SET DELETE_FLAG = 1
  where
		`DELETE_FLAG` = 0
		  
      and `CONSTANT_TYPE` = #{item.constantType}
    	  
    	  
      and `CONSTANT_NO` = #{item.constantNo}
